
public class loopdemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		    int y, x = 1, total = 0;
		    while (x <= 5) {
		      y = x * x;
		      System.out.println(y);
		      total = total + y;
		      x = x +1;
		    }
		    System.out.println("Total ist: " + total);
		 
	}

}
